				return [true];  // Enable all days, including Wednesdays
			},
			onSelect: function (dateText, inst) {
				var selectedDate = new Date(jQuery('.bat-date').datepicker('getDate'));
				var day = selectedDate.getDay();
				
				// Enable all time options first
				jQuery('.time-select option').prop('disabled', false);

				// Disable 1pm to 5pm only if it's a Wednesday
				if (day === 3) {
					jQuery('.time-select option').each(function () {
						var timeValue = jQuery(this).val();
						// Disable 1 PM to 5 PM for Wednesdays
						if (['1 PM', '2 PM', '3 PM', '4 PM', '5 PM'].includes(timeValue)) {
							jQuery(this).prop('disabled', true);
						}
					});
				}
			}
		});
	});

